import React, { useState, useEffect, Component } from 'react';
import { firebase } from '../../firebase';
import ReactHtmlParser from 'react-html-parser';
import style from './posts.module.css';
import PostInfo from './postinfo';
import { CSSTransition, TransitionGroup } from 'react-transition-group';

class Posts extends Component {

    state ={
        posts : [],
        showModal: false,
        clickedItem: []
    }

    componentWillMount(){
        this.handleOpenModal = this.handleOpenModal.bind(this);
        this.handleCloseModal = this.handleCloseModal.bind(this);
        firebase.firestore().collection('posts')
        .get()
        .then(querySnapshot => {
            querySnapshot.forEach(documentSnapshot => {
                this.setState(prevState => ({
                    posts: [...prevState.posts, documentSnapshot.data()]
                }))
            })
        })
    }

    getAuthor = () => {
        let template = null;
        template = this.state.posts.map( (item, i) => (
            <div>
                {item.author}
            </div>
        ))
        return template; 
    }

    getTitle = () => {
        let template = null;
        template = this.state.posts.map( (item, i) => (
            <div>  
               {item.title}
            </div>
        ))
        return template; 
    }

    getPosts = () => {
        let template = null;
        template = this.state.posts.map( (item, i) => (
        <CSSTransition
            classNames={{
                enter:style.postList_wrapper,
                enterActive:style.postList_wrapper_enter
            }}
            timeout={500}
            key={i}
        >
            <div className={style.postlist_item}>
                <PostInfo author={item.author} title={item.title} body={item.body.slice(3,-4)}/>
                <h2>{item.title}</h2>
                {/* <div>
                    <h3><strong>{item.title}</strong></h3>
                </div>
                <div>
                    {item.body.slice(3,-4)}
                </div>
                <div>
                    {item.author}
                </div> */}
            </div>
        </CSSTransition>
        ))
        return template;
    }



    render() {
        return(
            <div>
                <TransitionGroup
                    component="div"
                    className="list"
                >

                {this.getPosts()}
                </TransitionGroup>
            </div>
        )
    }
}
export default Posts;
