import React, { Component } from 'react';
import { CSSTransition, TransitionGroup } from 'react-transition-group';
import { Link } from 'react-router-dom';
import axios from 'axios';
import ReactModal from 'react-modal';

import { NEWS_URL, NEWS_KEY } from '../../../config';
import style from './newsList.module.css';
import Button from '../Buttons/buttons';
import ListInfo from '../../widgets/ListInfo/listInfo';

class NewsList extends Component {

    state = {
        items:[],
        country: this.props.country,
        city: this.props.city,
        category: this.props.category,
        amount: this.props.amount,
        showModal: false
    }

    handleOpenModal() {
        this.setState({showModal: true})
    }
    handleCloseModal() {
        this.setState({showModal: false})
    }

    componentWillMount(){
        this.handleOpenModal = this.handleOpenModal.bind(this);
        this.handleCloseModal = this.handleCloseModal.bind(this);
        this.request(this.state.country, this.state.city, this.state.category, this.state.amount)
    }

    loadMore =() => {
        this.state.amount = this.state.amount + 5;
        this.request(this.request(this.state.country, this.state.city, this.state.category, this.state.amount))
    }

    request = (country, city, category, amount ) => {
        axios.get(`${NEWS_URL}country=${country}&category=${category}&pageSize=${amount}&q=${city}&apiKey=${NEWS_KEY}`)
        .then( response => {
            this.setState({
                items:response.data.articles
            })
        })
    }

    handleOpenModal(item) {
        this.setState({showModal: true})
    }
    handleCloseModal() {
        this.setState({showModal: false})
    }

    renderNews = (type) => {
        let template = null;
        switch(type){
            case('detail'):
                template = this.state.items.map( (item, i) => (
                    <CSSTransition
                        classNames={{
                            enter:style.newsList_wrapper,
                            enterActive:style.newsList_wrapper_enter
                        }}
                        timeout={500}
                        key={i}
                    >
                        <div>
                            <div className={style.newslist_item} onClick={this.handleOpenModal(item)}>
                                {/* <Link to={{
                                    pathname: `articles/${i}`,
                                    state: {
                                        data: item
                                    }
                                }}> */}
                                {/* <a href={item.url}> */}
                                    <ListInfo provider={item.source.name} date={item.publishedAt}/>
                                    <h2>{item.title}</h2>
                                {/* </a> */}
                                {/* </Link> */}
                            </div>
                        </div>
                    </CSSTransition>
                ))
                break;
            case('cardMain'):
                template = this.state.items.map( (item, i) => (
                    <CSSTransition
                        classNames={{
                            enter:style.newsList_wrapper,
                            enterActive:style.newsList_wrapper_enter
                        }}
                        timeout={500}
                        key={i}
                    >
                        <Link to={{
                            pathname:`articles/${i}`,
                            state: {
                                data:item
                            }
                        }}>
                            <div className={style.flex_wrapper}>
                                <div className={style.left}
                                    style={{
                                        background: `url(${item.urlToImage})`
                                    }}>
                                    <div></div>
                                </div>
                                <div className={style.right}>
                                    <ListInfo provider={item.source.name} date={item.publishedAt}/>
                                    <h2>{item.title}</h2>
                                </div>
                            </div>
                        </Link>
                    </CSSTransition>
                ))
                break;
            default:
                template=null;
        }
        return template;
    }

    render() {
        //console.log(this.state.items)
        // let header;
        // if(this.props.location.state.data.author == null){
        //     header = <Header 
        //     date={this.props.location.state.data.publishedAt}
        //     source={this.props.location.state.data.source}
        //     author='N/A'
        //     title={this.props.location.state.data.title}
        //     url={this.props.location.state.data.url}
        //     />
        // }else{
        //     header = <Header 
        //     date={this.props.location.state.data.publishedAt}
        //     source={this.props.location.state.data.source}
        //     author={this.props.location.state.data.author}
        //     title={this.props.location.state.data.title}
        //     url={this.props.location.state.data.url}
        //     />
        // }
        return(
            <div>
                <ReactModal
                    isOpen={this.state.showModal}
                    contentLabel="Minimal Modal Example"
                >
                    <button onClick={this.handleCloseModal}>Close Modal</button>
                    <div className={style.articleWrapper}>
                        {/* {header} */}

                    </div>
                </ReactModal>

                <TransitionGroup
                    component="div"
                    className="list"
                >
                    { this.renderNews( this.props.type )}
                </TransitionGroup>
                <Button
                    type="loadmore"
                    loadMore={()=>this.loadMore()}
                    cta="Load More News"
                />
            </div>
        )
    }
}

export default NewsList;
