import React, { Component } from 'react';
import style from './videosList.module.css';
import axios from 'axios';

import { YOUTUBE_URL, YOUTUBE_KEY, NEWS_KEY } from '../../../config';
import Button from '../Buttons/buttons';
import VideosListTemplate from './videosListTemplate';
import YouTube from 'react-youtube';
import Dailymotion from 'react-dailymotion';

class VideosList extends Component {
    state = {
        videos: {},
        query: this.props.query,
        amount: this.props.amount,
    }

    renderTitle = () => {
        return this.props.title ? 
            <h3><strong>COVID-19</strong> Videos</h3>
            : null
    }

    componentWillMount(){
        this.request(this.state.query)
    }

    request = (query) => {
        axios.get(`${YOUTUBE_URL}/search?part=snippet&q=${query}&order=rating&type=video&videoDefinition=any&key=${YOUTUBE_KEY}`)
        .then( response => {
            this.setState({
                videos: response.data.items
            })
        })
    } 



    renderVideos = () => {
        let template = null;
        switch(this.props.type){
            case('card'):
                template = <VideosListTemplate data={this.state.videos}/>
                break;
            default:
                template = null
        }
        return template;
    }

    loadMore = () => {

    }

    renderButton = () => {
        return this.props.loadmore ? 
            <Button
                type="loadmore"
                loadMore={() => this.loadMore()}
                cta="Load More Videos"
            />
            : 
            <Button type="linkTo" cta="More Videos" linkTo="/videos"/>
    }

    render(){
        return(
            <div className={style.videosList_wrapper}>
                { this.renderTitle() }
                { this.renderVideos() }
                { this.renderButton() }
            </div>
        )
    }
}

export default VideosList;
