import React, {Component} from 'react';

import NewsSlider from '../widgets/NewsSlider/slider';
import NewsList from '../widgets/NewsList/newsList';
import VideosList from '../widgets/VideosList/videosList';

import {query, order, duration, videoType, definition, country, city, category} from '../../config';

import Select from 'react-select';

const options = [
    { value: 'en', label: 'en'},
    { value: 'kr', label:'kr'}
]

class Home extends Component{
    state = {
        selectedOption: null,
        selectedValue: ''
    };
    handleChange = selectedOption => {
        this.setState(
            {selectedOption},
            () => this.setState({
                selectedValue: selectedOption.value
            })
        );
    }
    render(){
        const {selectedOption} = this.state.selectedValue
        console.log(this.state.selectedValue)
        return(
            <div>
                <div>
                    <Select
                        value={selectedOption}
                        onChange={this.handleChange}
                        options={options}
                    />
                </div>
                <NewsSlider
                    type="featured"
                    country={country}
                    city={city}
                    category={category}
                    amount={5}
                    setting={{
                        dots:true
                    }}
                />
                <NewsList
                    type="detail"
                    country={country}
                    city={city}
                    category={category}
                    amount={5}
                    loadmore={true}
                />
                <VideosList
                    type="card"
                    query={query}
                    order={order}
                    duration={duration}
                    videoType={videoType}
                    definition={definition}
                    maxResults={5}
                    language={this.state.selectedValue}
                    title={true}
                    loadmore={true}
                />
            </div>
        )
    }
}

export default Home;
