import React, { Component } from 'react';
import FormField from '../widgets/FormFields/formFields';
import styles from './dashboard.module.css';

import { Editor } from 'react-draft-wysiwyg';
import { EditorState, convertFromRaw, convertToRaw } from 'draft-js';
import { stateToHTML } from 'draft-js-export-html';
import { firebase } from '../../firebase';

class Dashboard extends Component {
    state = {
        updatedContent: [],
        editorState: EditorState.createEmpty(),
        registerError: '',
        loading: false,
        formdata:{
            author:{
                element:'input',
                value:'',
                config:{
                    name:'author_input',
                    type:'text',
                    placeholder:'Enter your name'
                },
                validation:{
                    required:true
                },
                valid:false,
                touched:false,
                validationMessage:''
            },
            title:{
                element:'input',
                value:'',
                config:{
                    name:'title_input',
                    type:'text',
                    placeholder:'Enter your title'
                },
                validation:{
                    required:true
                },
                valid:false,
                touched:false,
                validationMessage:''
            },
            body:{
                element: 'texteditor',
                value:'',
                valid:true
            }
        }
    }

    updateForm = (element, content='') => {
        const newFormdata = {
            ...this.state.formdata
        }
        const newElement = {
            ...newFormdata[element.id]
        }

        if(content === ''){
            newElement.value = content;
        }else{
            newElement.value = content;
        }

        if(element.blur){
            let validData = this.validate(newElement);
            newElement.valid = validData[0];
            newElement.validationMessage = validData[1];   
        }
        newElement.touched = element.blur;
        newFormdata[element.id] = newElement;

        this.setState({
            formdata:newFormdata
        })
    }

    validate = (element) => {
        let error = [true,''];

        if(element.validation.required){
            const valid = element.value.trim() !=='';
            const message = `${!valid ? 'This field is required':''}`;
            error = !valid ? [valid,message] : error
        }

        return error;
    }

    submitForm = (event) => {
        event.preventDefault();

        let dataToSubmit = {};
        let formIsValid = true;

        for(let key in this.state.formdata){
            dataToSubmit[key] = this.state.formdata[key].value
        }
        for(let key in this.state.formdata){
            formIsValid = this.state.formdata[key].valid && formIsValid;
        }

        if(formIsValid){
            this.setState({
                loading:true,
                postError:''
            })
            firebase.firestore().collection('posts').add({
                author: dataToSubmit.author,
                title: dataToSubmit.title,
                body: dataToSubmit.body
            }).then( article => {
                this.props.history.push(`/dashboard`)
            }).catch( e => {
                this.setState({
                    postError: e.message
                })
            }) 
            console.log("SUCCESS")

        }else{
            this.setState({
                postError: 'Something went wrong'
            })
        }
    }

    submitButton = () => (
        this.state.loading ?
            'loading...'
        :
        <div>
            <button type="submit"> Add post </button>
        </div>
    )

    showError = () => (
        this.state.registerError !== '' ? 
            <div className={styles.error}>{this.state.registerError}</div>
        : ''
    )

    onEditorStateChange = (editorState) => {
        let contentState = editorState.getCurrentContent();
        let rawState = convertToRaw(contentState)
        rawState.blocks.map((item) => {
            this.setState({
                updatedContent: item.text
            })
        })
        //console.log(this.state.updatedContent)
        this.updateForm({id:'body'}, this.state.updatedContent)
        // let html = stateToHTML(contentState)

        this.setState({
            editorState
        })
    }


    render() {
        return(
            <div className={styles.postContainer}>
                <form onSubmit={this.submitForm}>
                    <h2>Add Post</h2>

                    <FormField
                        id={'author'}
                        formdata={this.state.formdata.author}
                        change={(element)=>this.updateForm(element)}
                    />
                    <FormField
                        id={'title'}
                        formdata={this.state.formdata.title}
                        change={(element)=>this.updateForm(element)}
                    />

                    <Editor
                        editorState={this.state.editorState}
                        wrapperClassName="myEditor-wrapper"
                        editorClassName="myEditor-editor"
                        onEditorStateChange={this.onEditorStateChange}
                    />

                    {this.submitButton()}
                    {this.showError()}
                </form>               
            </div>
        )
    }
}

export default Dashboard;
