import React, { Component } from 'react';
import { CSSTransition, TransitionGroup } from 'react-transition-group';
import { Link } from 'react-router-dom';
import axios from 'axios';

import { URL, KEY } from '../../../config';
import style from './newsList.module.css';

class NewsList extends Component {
    state = {
        items:[],
        country: this.props.country,
        city: this.props.city,
        category: this.props.category,
        amount: this.props.amount
    }

    componentWillMount(){
        this.request(this.state.country, this.state.city, this.state.category, this.state.amount)
    }

    loadMore =() => {

    }

    request = (country, city, category, amount ) => {
        axios.get(`${URL}country=${country}&category=${category}&pageSize=${amount}&q=${city}&apiKey=${KEY}`)
        .then( response => {
            this.setState({
                items:response.data.articles
            })
        })
    }



    renderNews = (type) => {
        let template = null;

        switch(type){
            case('detail'):
                template = this.state.items.map( (item, i) => (
                    <CSSTransition
                        classNames={{
                            enter:style.newsList_wrapper,
                            enterActive:style.newsList_wrapper_enter
                        }}
                        timeout={100000}
                        key={i}
                    >
                        <div>
                            <div className={style.newslist_item}>
                                <Link to={`articles/${i}`}>
                                    <h2>{item.title}</h2>
                                </Link>
                            </div>
                        </div>
                    </CSSTransition>
                ))
                break;
            default:
                template=null;
        }
        return template;
    }

    render() {
        return(
            <div>
                <TransitionGroup
                    component="div"
                    className="list"
                >
                    { this.renderNews( this.props.type )}
                </TransitionGroup>
                <div onClick={() => this.loadMore()}>
                    LOAD MORE
                </div>
            </div>
        )
    }
}

export default NewsList;
