import React, { Component } from 'react';
import FormField from '../widgets/FormFields/formFields';
import styles from './dashboard.module.css';

import { Editor } from 'react-draft-wysiwyg';
import { EditorState, convertFromRaw, convertToRaw } from 'draft-js';
import { stateToHTML } from 'draft-js-export-html';

class Dashboard extends Component {
    state = {
        editorState: EditorState.createEmpty(),
        registerError: '',
        loading: false,
        formdata:{
            author:{
                element:'input',
                value:'',
                config:{
                    name:'author_input',
                    type:'text',
                    placeholder:'Enter your name'
                },
                validation:{
                    required:true
                },
                valid:false,
                touched:false,
                validationMessage:''
            },
            title:{
                element:'input',
                value:'',
                config:{
                    name:'title_input',
                    type:'text',
                    placeholder:'Enter your title'
                },
                validation:{
                    required:true
                },
                valid:false,
                touched:false,
                validationMessage:''
            },
            body:{
                element: 'texteditor',
                value:'',
                valid:true
            },
            country:{
                element:'select',
                value:'',
                config:{
                    name:'country_input',
                    options:[]
                },
                validation:{
                    required:true
                },
                valid:false,
                touched:false,
                validationMessage:''
            }
        }
    }

    componentDidMount(){
        this.loadCountry()
    }

    loadCountry= () => {
        let countries = [];
        let country_json = {
            "country:":[
                {id:1, name:'Canada'},
                {id:2, name:'USA'},
                {id:3, name:'USA'},
                {id:4, name:'USA'},
                {id:5, name:'USA'},
                {id:6, name:'USA'},
                {id:7, name:'USA'},
                {id:8, name:'USA'},
                {id:9, name:'USA'},
                {id:10, name:'USA'},
                {id:11, name:'USA'},
                {id:12, name:'USA'}
            ]
        }
        console.log(country_json.country)

        countries.push({id:1, name:'Canada'})

        const newFormdata = {...this.state.formdata}
        const newElements = {...newFormdata['country']}
        newElements.config.options = countries;
        newFormdata['country'] = newElements;
        this.setState({
            formdata: newFormdata
        })
    }

    updateForm = (element, content='') => {
        const newFormdata = {
            ...this.state.formdata
        }
        const newElement = {
            ...newFormdata[element.id]
        }

        if(content === ''){
            newElement.value = element.event.target.value;
        }else{
            newElement.value = content;
        }

        if(element.blur){
            let validData = this.validate(newElement);
            newElement.valid = validData[0];
            newElement.validationMessage = validData[1];   
        }
        newElement.touched = element.blur;
        newFormdata[element.id] = newElement;

        this.setState({
            formdata:newFormdata
        })
    }

    validate = (element) => {
        let error = [true,''];

        if(element.validation.required){
            const valid = element.value.trim() !=='';
            const message = `${!valid ? 'This field is required':''}`;
            error = !valid ? [valid,message] : error
        }

        return error;
    }

    submitForm = (event) => {
        event.preventDefault();

        let dataToSubmit = {};
        let formIsValid = true;

        for(let key in this.state.formdata){
            dataToSubmit[key] = this.state.formdata[key].value
        }
        for(let key in this.state.formdata){
            formIsValid = this.state.formdata[key].valid && formIsValid;
        }
        console.log(dataToSubmit)

        if(formIsValid){
            console.log('SUBMIT POST')
        }else{
            this.setState({
                postError: 'Something went wrong'
            })
        }
    }

    submitButton = () => (
        this.state.loading ?
            'loading...'
        :
        <div>
            <button type="submit"> Add post </button>
        </div>
    )

    showError = () => (
        this.state.registerError !== '' ? 
            <div className={styles.error}>{this.state.registerError}</div>
        : ''
    )

    onEditorStateChange = (editorState) => {
        let contentState = editorState.getCurrentContent();
        let rawState = convertToRaw(contentState)
        let html = stateToHTML(contentState)

        this.updateForm({id:'body'}, html)

        this.setState({
            editorState
        })
    }


    render() {
        return(
            <div className={styles.postContainer}>
                <form onSubmit={this.submitForm}>
                    <h2>Add Post</h2>

                    <FormField
                        id={'author'}
                        formdata={this.state.formdata.author}
                        change={(element)=>this.updateForm(element)}
                    />
                    <FormField
                        id={'title'}
                        formdata={this.state.formdata.title}
                        change={(element)=>this.updateForm(element)}
                    />

                    <Editor
                        editorState={this.state.editorState}
                        wrapperClassName="myEditor-wrapper"
                        editorClassName="myEditor-editor"
                        onEditorStateChange={this.onEditorStateChange}
                    />

                    <FormField
                        id={'Country'}
                        formdata={this.state.formdata.country}
                        change={(element)=>this.updateForm(element)}
                    />

                    {this.submitButton()}
                    {this.showError()}
                </form>               
            </div>
        )
    }
}

export default Dashboard;
