import * as firebase from 'firebase';

var firebaseConfig = {
    apiKey: "AIzaSyAIOTgofwh0fzL96KkyDbANV5Gokcm6pag",
    authDomain: "cmpt35.firebaseapp.com",
    databaseURL: "https://cmpt35.firebaseio.com",
    projectId: "cmpt35",
    storageBucket: "cmpt35.appspot.com",
    messagingSenderId: "1026091044307",
    appId: "1:1026091044307:web:b6ce6c8aaf20c98e9fb326"
};

firebase.initializeApp(firebaseConfig);

const firebaseDB = firebase.database().ref();
const firebasePosts = firebaseDB.child('post');

const firebaseLooper = (snapshot) => {
    const data = [];
    snapshot.forEach((childSnapshot)=>{
        data.push({
            ...childSnapshot.val(),
            id:childSnapshot.key
        })
    });
    return data;
}


export {
    firebase,
    firebaseDB,
    firebasePosts,
    firebaseLooper
}
