    authDomain: "cmptweb.firebaseapp.com",
    databaseURL: "https://cmptweb.firebaseio.com",
    projectId: "cmptweb",
    storageBucket: "cmptweb.appspot.com",
    messagingSenderId: "284497952543",
    appId: "1:284497952543:web:63c34d0957f614d9bd3a2a"
  };

firebase.initializeApp(firebaseConfig);

const firebaseDB = firebase.database();
const firebasePosts = firebaseDB.ref('posts');

const firebaseLooper = (snapshot) => {
    const data = [];
    snapshot.forEach((childSnapshot)=>{
        data.push({
            ...childSnapshot.val(),
            id:childSnapshot.key
        })
    });
    return data;
}


export {
    firebase,
    firebaseDB,
    firebaseLooper
}
