import React, { Component } from 'react';
import axios from 'axios';

import SliderTemplates from './slider_template';

class NewsSlider extends Component {

    state ={
        news: []
    }

    componentWillMount() {
        //axios.get('http://localhost:3004/articles? start=0&_end=3')
        axios.get('http://newsapi.org/v2/everything?q=covid&from=2020-03-10&sortBy=publishedAt&apiKey=db898a9e78a949d68a2aeb1466c43a70')
        .then( response => {
            this.setState({
                news:response.data
            })
        })
    }

    render() {
        result = JSON.parse(this.state.news)
        return(
            <SliderTemplates data={result} type="featured"/>
        )
    }
}

export default NewsSlider;
