import React, { useState, useEffect } from 'react';
import { firebase } from '../../firebase';

function usePosts() {
    const[author, title, body] = useState([])
    useEffect(() => {
        firebase
            .firestore()
            .collection('posts')
            .onSnapshot((snapshot) => {
                const newPost = snapshot.docs.map((doc) => {
                    console.log(doc)
                })
                setPosts(newPost)
            })
    }, [])
    return body
}

const Posts = () => {
    const posts = usePosts()
    return(
        <div>
            <h2>Posts List</h2>
            <ol>
                {posts.map((post) => {
                    <div>
                        {post.body}
                    </div>
                })}
            </ol>
        </div>
    )
}

export default Posts;
